using System;using System.Collections.Generic;using System.IO;using System.Linq;using System.Text;using System.Threading.Tasks;namespace AssessmentS{    class ArrayCSV    {        static void Main(string[] args)        {            string[,] files = new string[2, 5];            files[0, 0] = "Hi";            files[0, 1] = "this";            files[0, 2] = "is";            files[0, 3] = "varsha";            files[0, 4] = "hospet";            files[1, 0] = "abcde";            files[1, 1] = "11";            files[1, 2] = "77";            files[1, 3] = "mysore";            files[1, 4] = "765432";            StreamWriter sw = File.CreateText("OutputText.csv");            for (int i = 0; i < 2; i++)            {                for (int j = 0; j < 5; j++)                {                    sw.Write(files[i, j] + ";");                }                sw.Write("\n");            }            sw.Flush();            sw.Close();            Console.WriteLine("successfully completed");        }    }}