
using System;using System.IO;namespace Assignments{    class UppertoLower    {        [STAThread]        static void Main(string[] args)        {            String line;            try            {

                StreamReader a = new StreamReader("TextFile.txt");                line = a.ReadLine();                while (line != null)                {                    Console.WriteLine(line);                    line = a.ReadLine();                }                a.Close();                Console.ReadLine();            }            catch (Exception e)            {                Console.WriteLine("Exception: " + e.Message);            }            finally            {                Console.WriteLine("Executing finally block.");            }        }    }}